const SOURCE_NODE_NAME = "Get Data"; // Nome do n√≥ de origem
const MAX_ITERATIONS = 20; // N√∫mero m√°ximo de itera√ß√µes para prevenir loops infinitos

// Array para armazenar todos os n√≥s combinados
let allNodes = [];
let projectData = null;
let totalPagesProcessed = 0;

console.log(`Iniciando coleta de dados de m√∫ltiplas execu√ß√µes do n√≥: ${SOURCE_NODE_NAME}`);

// Loop para coletar todas as sa√≠das dispon√≠veis
let iteration = 0;
let hasMoreData = true;

while (hasMoreData && iteration < MAX_ITERATIONS) {
  try {
    // Tenta obter os dados da itera√ß√£o atual
    const pages = $items(SOURCE_NODE_NAME, 0, iteration);

    if (!pages || pages.length === 0) {
      console.log(`Nenhum dado encontrado na itera√ß√£o ${iteration}. Finalizando coleta.`);
      hasMoreData = false;
      continue;
    }

    console.log(`Processando dados da itera√ß√£o ${iteration}. Encontrados ${pages.length} itens.`);

    // Processa cada item encontrado na itera√ß√£o
    for (const item of pages) {
      // Verifica se temos os dados no formato esperado
      const project = item.json?.data?.organization?.projectV2;
      const nodes = project?.items?.nodes;

      if (project && nodes) {
        // Se ainda n√£o temos metadados do projeto, salvamos da primeira ocorr√™ncia v√°lida
        if (!projectData) {
          projectData = project;
          console.log(`Metadados do projeto obtidos: ${project.title} (ID: ${project.id})`);
        }

        // Adiciona os n√≥s encontrados ao array principal
        console.log(`Adicionando ${nodes.length} novos itens ao conjunto de dados`);
        allNodes.push(...nodes);
        totalPagesProcessed++;
      } else {
        console.log(`Aviso: Formato de dados inesperado na itera√ß√£o ${iteration}`);
      }
    }

    // Avan√ßa para a pr√≥xima itera√ß√£o
    iteration++;

  } catch (error) {
    // Se ocorrer um erro, significa que n√£o h√° mais dados dispon√≠veis
    console.log(`Fim dos dados dispon√≠veis ap√≥s ${iteration} itera√ß√µes. Erro: ${error.message}`);
    hasMoreData = false;
  }
}

// Verifica se conseguimos coletar algum dado
if (!projectData || allNodes.length === 0) {
  console.error("Erro: Nenhum dado v√°lido foi encontrado em qualquer itera√ß√£o");
  return [{
    json: {
      error: true,
      message: "N√£o foi poss√≠vel coletar dados v√°lidos"
    }
  }];
}

// Estat√≠sticas finais
console.log(`Coleta de dados conclu√≠da:`);
console.log(`- Total de itera√ß√µes processadas: ${iteration}`);
console.log(`- Total de p√°ginas v√°lidas: ${totalPagesProcessed}`);
console.log(`- Total de itens coletados: ${allNodes.length}`);
console.log(`- Dados do projeto: ${projectData.title} (ID: ${projectData.id})`);

// Constr√≥i o resultado final no formato esperado pelo process_data.js
const result = {
  data: {
    organization: {
      projectV2: {
        id: projectData.id,
        title: projectData.title,
        fields: projectData.fields,
        items: {
          nodes: allNodes,
        },
      },
    },
  },
};

// Retorna o resultado consolidado
return [{ json: result }];

