// Formata√ß√£o de email para relat√≥rio de √©picos
// Este script formata os dados processados em um email HTML otimizado para Gmail

function exists(value) {
	return value !== null && value !== undefined && value !== '';
}

function formatEpicsEmail(summary) {
	if (!summary || summary.error) {
		return {
			subject: 'Erro no Relat√≥rio de √âpicos',
			html: `
				<div style="font-family: Arial, sans-serif; max-width: 800px; margin: 0 auto;">
					<h1 style="color: #d32f2f;">Erro no Relat√≥rio de √âpicos</h1>
					<p style="color: #666;">${
						summary?.message ||
						'Erro desconhecido ao processar dados dos √©picos'
					}</p>
				</div>
			`,
		};
	}

	// CSS inline para melhor compatibilidade com clientes de email
	const css = `
		<style type="text/css">
			body, div, p, h1, h2, h3 { margin: 0; padding: 0; }
			body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; background-color: #f7f7f7; }
			.container { max-width: 900px; margin: 0 auto; background-color: #ffffff; }
			.header { background: #667eea; color: white; padding: 30px; }
			.content { padding: 20px; }
			.stat-card { background: #f8f9fa; border: 1px solid #e9ecef; border-radius: 8px; padding: 20px; margin-bottom: 20px; text-align: center; }
			.progress-bar { background: #e9ecef; border-radius: 10px; overflow: hidden; height: 20px; margin: 10px 0; }
			.progress-fill { height: 100%; transition: width 0.3s ease; }
			.epic-item { background: white; border: 1px solid #dee2e6; border-radius: 8px; margin: 15px 0; padding: 20px; }
			.epic-title { font-size: 18px; font-weight: bold; color: #212529; margin: 0; }
			.epic-status { padding: 4px 12px; border-radius: 20px; font-size: 12px; font-weight: bold; text-transform: uppercase; }
			.status-completed { background: #d4edda; color: #155724; }
			.status-in-progress { background: #fff3cd; color: #856404; }
			.status-not-started { background: #e2e3e5; color: #383d41; }
			.footer { background: #f8f9fa; padding: 20px; text-align: center; color: #6c757d; font-size: 14px; }
			table { border-collapse: collapse; width: 100%; }
			td { vertical-align: top; padding: 0; }
		</style>
	`;

	// Fun√ß√£o para obter cor da barra de progresso
	function getProgressColor(percentage) {
		if (percentage >= 80) return '#28a745';
		if (percentage >= 50) return '#ffc107';
		if (percentage >= 20) return '#fd7e14';
		return '#dc3545';
	}

	// Fun√ß√£o para obter classe do status
	function getStatusClass(status) {
		switch (status.toLowerCase()) {
			case 'completed':
				return 'status-completed';
			case 'in progress':
				return 'status-in-progress';
			default:
				return 'status-not-started';
		}
	}
	const today = new Date();

	// In√≠cio do HTML - usando tabelas para melhor compatibilidade com clientes de email
	let html = `
		<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">
		<html xmlns="http://www.w3.org/1999/xhtml">
		<head>
			<meta http-equiv="Content-Type" content="text/html; charset=UTF-8" />
			<meta name="viewport" content="width=device-width, initial-scale=1.0"/>
			<title>Relat√≥rio de Status dos √âpicos</title>
			${css}
		</head>
		<body style="margin: 0; padding: 0; font-family: Arial, sans-serif; background-color: #f7f7f7;">
			<table border="0" cellpadding="0" cellspacing="0" width="100%" style="max-width: 900px; margin: 0 auto; background-color: #ffffff;">
				<!-- Cabe√ßalho -->
				<tr>
					<td style="padding: 30px; background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white;">
						<h1 style="margin: 0; font-size: 28px;">üìä Relat√≥rio de Status dos √âpicos</h1>
						<div style="opacity: 0.9; margin-top: 10px;">
							${summary.projectTitle || 'Projeto'} - ${
		summary.generatedAt || new Date().toLocaleString('pt-BR')
	}
						</div>
					</td>
				</tr>

				<!-- Conte√∫do -->
				<tr>
					<td style="padding: 20px;">
						<!-- Cards de estat√≠sticas -->
						<table border="0" cellpadding="0" cellspacing="0" width="100%">
							<tr>
	`;

	// Cards de estat√≠sticas
	const statCards = [
		{
			title: 'Total de √âpicos',
			value: summary.totalEpics,
			color: '#667eea',
			// Format to be more prominent like the other numbers
			format: true,
		},
		{
			title: 'Taxa M√©dia de Conclus√£o',
			value: `${summary.statistics?.averageCompletionRate || 0}%`,
			color: '#28a745',
		},
		{
			title: 'Progresso Geral',
			value: `${summary.statistics?.overallProgress || 0}%`,
			color: getProgressColor(summary.statistics?.overallProgress || 0),
			hasProgress: true,
			progress: summary.statistics?.overallProgress || 0,
		},
		{
			title: 'Sub-issues Totais',
			value: summary.statistics?.totalSubIssues || 0,
			subtext: `${summary.statistics?.completedSubIssues || 0} conclu√≠das`,
			color: '#6c757d',
		},
	];

	// Gerar HTML para cada card de estat√≠stica
	statCards.forEach((card) => {
		html += `
								<td width="25%" style="padding: 10px;">
									<table border="0" cellpadding="0" cellspacing="0" width="100%" style="background: #f8f9fa; border: 1px solid #e9ecef; border-radius: 8px; text-align: center;">
										<tr>
											<td style="padding: 20px;">
												<h3 style="margin: 0 0 10px 0; color: #495057; font-size: 14px; text-transform: uppercase;">${
													card.title
												}</h3>
												<div style="font-size: 32px; font-weight: bold; margin: 10px 0; color: ${
													card.color
												};">${card.value}</div>
												${
													card.subtext
														? `<div style="font-size: 18px; color: #6c757d;">${card.subtext}</div>`
														: ''
												}
												${
													card.hasProgress
														? `
													<div style="background: #e9ecef; border-radius: 10px; overflow: hidden; height: 20px; margin: 10px 0;">
														<div style="width: ${card.progress}%; background: ${card.color}; height: 100%;"></div>
													</div>
												`
														: ''
												}
											</td>
										</tr>
									</table>
								</td>
		`;
	});

	html += `
							</tr>
						</table>
	`;

	// Distribui√ß√£o por status
	if (summary.epicsProgress && Object.keys(summary.epicsProgress).length > 0) {
		html += `
						<table border="0" cellpadding="0" cellspacing="0" width="100%" style="margin: 20px 0; background: #f8f9fa; border: 1px solid #e9ecef; border-radius: 8px;">
							<tr>
								<td style="padding: 20px;">
									<h3 style="margin: 0 0 15px 0; color: #495057; font-size: 16px; text-transform: uppercase; text-align: center;">Distribui√ß√£o por Status</h3>
									<table border="0" cellpadding="0" cellspacing="0" width="100%">
										<tr>
		`;

		// Calcular largura para cada status
		const statusCount = Object.keys(summary.epicsProgress).length;
		const columnWidth = 100 / statusCount;

		Object.entries(summary.epicsProgress).forEach(([status, count]) => {
			const percentage =
				summary.totalEpics > 0
					? Math.round((count / summary.totalEpics) * 100)
					: 0;

			// Define specific colors for each status
			let statusColor;
			// Replace spaces with non-breaking spaces in status
			const formattedStatus = status.replace(' ', '\u00A0');
			if (status.toLowerCase() === 'completed') {
				statusColor = '#28a745'; // Green for completed
			} else if (status.toLowerCase() === 'in progress') {
				statusColor = '#ffc107'; // Yellow for in progress
			} else if (status.toLowerCase() === 'not started') {
				statusColor = '#6c757d'; // Gray for not started
			} else {
				statusColor = getProgressColor(percentage); // Fallback
			}

			html += `
											<td width="${columnWidth}%" style="padding: 5px; text-align: center;">
												<div style="font-weight: bold; margin-bottom: 5px;">${formattedStatus}</div>
												<div style="font-size: 24px; color: ${statusColor};">${count}</div>
												<div style="font-size: 12px; color: #6c757d;">${percentage}%</div>
											</td>
			`;
		});

		html += `
										</tr>
									</table>
								</td>
							</tr>
						</table>
		`;
	}

	// Lista detalhada dos √©picos
	if (summary.epicsDetails && summary.epicsDetails.length > 0) {
		html += `
						<h2 style="margin: 30px 0 20px 0; color: #495057;">üìã Detalhes dos √âpicos</h2>
		`;

		summary.epicsDetails.forEach((epic) => {
			html += `
						<table border="0" cellpadding="0" cellspacing="0" width="100%" style="background: white; border: 1px solid #dee2e6; border-radius: 8px; margin: 15px 0;">
							<tr>
								<td style="padding: 20px;">
									<!-- Cabe√ßalho do √©pico -->
									<table border="0" cellpadding="0" cellspacing="0" width="100%">
										<tr>
											<td style="vertical-align: top;">
												<h3 style="font-size: 18px; font-weight: bold; color: #212529; margin: 0;">
													${
														epic.epicUrl !== '#'
															? `<a href="${epic.epicUrl}" style="color: #007bff; text-decoration: none;">#${epic.epicId}</a>`
															: `#${epic.epicId}`
													}
													${epic.epicTitle}
												</h3>
												<div style="font-size: 14px; color: #6c757d; margin: 5px 0;">
													Prioridade: ${epic.priority}
												</div>
											</td>
											<td style="text-align: right; vertical-align: top; width: 100px;">
												<span style="padding: 4px 12px; border-radius: 20px; font-size: 12px; font-weight: bold; text-transform: uppercase; background: ${
													epic.status.toLowerCase() === 'completed'
														? '#d4edda'
														: epic.status.toLowerCase() === 'in progress'
														? '#fff3cd'
														: '#e2e3e5'
												}; color: ${
				epic.status.toLowerCase() === 'completed'
					? '#155724'
					: epic.status.toLowerCase() === 'in progress'
					? '#856404'
					: '#383d41'
			};">${epic.status.replace(' ', '\u00A0')}</span>
											</td>
										</tr>
									</table>

									<!-- Progresso das sub-issues -->
									<table border="0" cellpadding="0" cellspacing="0" width="100%" style="margin: 15px 0;">
										<tr>
											<td style="padding-bottom: 5px;">
												<table border="0" cellpadding="0" cellspacing="0" width="100%">
													<tr>
														<td style="font-weight: bold;">Progresso das Sub-issues</td>
														<td style="text-align: right; font-weight: bold; color: ${getProgressColor(
															epic.completionRate,
														)};">${epic.completionRate}%</td>
													</tr>
												</table>
											</td>
										</tr>
										<tr>
											<td>
												<div style="background: #e9ecef; border-radius: 10px; overflow: hidden; height: 20px;">
													<div style="width: ${epic.completionRate}%; background: ${getProgressColor(
				epic.completionRate,
			)}; height: 100%;"></div>
												</div>
												<div style="font-size: 14px; color: #6c757d; margin-top: 5px;">
													${epic.subIssuesCompleted} de ${epic.subIssuesTotal} sub-issues conclu√≠das
													${epic.subIssuesRemaining > 0 ? ` (${epic.subIssuesRemaining} restantes)` : ''}
												</div>
											</td>
										</tr>
									</table>
			`;

			// Descri√ß√£o (se existir)
			if (epic.description) {
				html += `
									<div style="color: #6c757d; font-style: italic; margin: 10px 0;">
										${epic.description}
									</div>
				`;
			}

			// Respons√°veis (se existirem)
			if (epic.assignees && epic.assignees.length > 0) {
				html += `
									<div style="margin: 10px 0;">
										<strong style="font-size: 14px;">Respons√°veis:</strong>
										<div style="margin-top: 5px;">
				`;

				epic.assignees.forEach((assignee) => {
					html += `
											<span style="display: inline-block; background: #e9ecef; padding: 2px 8px; border-radius: 12px; font-size: 12px; margin-right: 5px; margin-bottom: 5px;">@${assignee}</span>
					`;
				});

				html += `
										</div>
									</div>
				`;
			}

			// Labels (se existirem)
			if (epic.labels && epic.labels.length > 0) {
				html += `
									<div style="margin: 10px 0;">
										<strong style="font-size: 14px;">Labels:</strong>
										<div style="margin-top: 5px;">
				`;

				epic.labels.forEach((label) => {
					html += `
											<span style="display: inline-block; background: #6f42c1; color: white; padding: 2px 8px; border-radius: 4px; font-size: 11px; margin-right: 5px; margin-bottom: 5px;">${label}</span>
					`;
				});

				html += `
										</div>
									</div>
				`;
			}

			// Datas de cria√ß√£o/atualiza√ß√£o
			html += `
									<div style="margin-top: 15px; font-size: 12px; color: #868e96;">
										Criado: ${new Date(epic.createdAt).toLocaleDateString('pt-BR')} |
										Atualizado: ${new Date(epic.updatedAt).toLocaleDateString('pt-BR')}
									</div>
								</td>
							</tr>
						</table>
			`;
		});
	} else {
		html += `
						<table border="0" cellpadding="0" cellspacing="0" width="100%" style="margin: 40px 0;">
							<tr>
								<td style="text-align: center; padding: 40px; color: #6c757d;">
									<p>Nenhum √©pico encontrado no projeto.</p>
								</td>
							</tr>
						</table>
		`;
	}

	// Rodap√©
	html += `
					</td>
				</tr>
				<tr>
					<td style="background: #f8f9fa; padding: 20px; text-align: center; color: #6c757d; font-size: 14px;">
						<p>Relat√≥rio gerado automaticamente em ${
							summary.date
								? new Date(summary.date).toLocaleString('pt-BR')
								: new Date().toLocaleString('pt-BR')
						}</p>
						<p style="font-size: 12px; margin-top: 10px;">Este relat√≥rio analisa √©picos com base em suas sub-issues e progresso.</p>
					</td>
				</tr>
			</table>
		</body>
		</html>
	`;

	return {
		subject: `üìä Relat√≥rio de √âpicos - ${today.toLocaleDateString('pt-BR')}`,
		html: html,
	};
}

// Execu√ß√£o
try {
	let summary;

	// Verificar se est√° rodando no n8n ou localmente
	if (typeof $input !== 'undefined') {
		// Ambiente n8n
		const inputData = $input.first()?.json;
		if (!inputData) {
			throw new Error('Nenhum dado de entrada fornecido');
		}
		summary = inputData;
	} else {
		// Ambiente local - ler do arquivo de resultado
		const fs = require('fs');
		if (!fs.existsSync('epics_analysis_result.json')) {
			throw new Error(
				'Arquivo epics_analysis_result.json n√£o encontrado! Execute process_data.js primeiro.',
			);
		}
		const rawData = fs.readFileSync('epics_analysis_result.json', 'utf8');
		summary = JSON.parse(rawData);
	}

	const emailContent = formatEpicsEmail(summary);

	// Para ambiente local, exibir resultado
	if (typeof module !== 'undefined' && module.exports) {
		console.log('\n=== EMAIL FORMATADO ===');
		console.log('Subject:', emailContent.subject);
		console.log('\nHTML Content gerado com sucesso!');
		console.log('Total de caracteres:', emailContent.html.length);

		// Salvar HTML em arquivo para visualiza√ß√£o
		const fs = require('fs');
		fs.writeFileSync('epic_report_email.html', emailContent.html);
		console.log('Email HTML salvo em epic_report_email.html');

		module.exports = emailContent;
	} else {
		// Ambiente n8n
		return emailContent;
	}
} catch (error) {
	console.error('Erro na formata√ß√£o do email:', error.message);

	const errorEmail = {
		subject: 'Erro no Relat√≥rio de √âpicos',
		html: `
			<div style="font-family: Arial, sans-serif; max-width: 800px; margin: 0 auto; padding: 20px;">
				<h1 style="color: #d32f2f;">‚ùå Erro no Relat√≥rio de √âpicos</h1>
				<p style="color: #666; font-size: 16px;">Ocorreu um erro ao formatar o relat√≥rio:</p>
				<div style="background: #f5f5f5; padding: 15px; border-radius: 5px; font-family: monospace;">
					${error.message}
				</div>
				<p style="color: #666; margin-top: 20px;">
					Verifique os dados de entrada e tente novamente.
				</p>
			</div>
		`,
	};

	if (typeof module !== 'undefined' && module.exports) {
		console.log(JSON.stringify(errorEmail, null, 2));
		process.exit(1);
	} else {
		return errorEmail;
	}
}

// Verifica se √© ambiente local ou n8n
const isLocalRun =
	typeof module !== 'undefined' && module.exports && require.main === module;

// Fun√ß√£o para verificar se um valor existe
function exists(value) {
	return value !== null && value !== undefined && value !== '';
}

// Fun√ß√£o para verificar se o t√≠tulo cont√©m palavras-chave t√≠picas de √©picos
function determineEpicStatus(subIssuesSummary) {
	if (!subIssuesSummary || subIssuesSummary.total === 0) {
		return 'Not Started';
	}

	if (subIssuesSummary.percentCompleted === 100) {
		return 'Completed';
	} else if (subIssuesSummary.percentCompleted > 0) {
		return 'In Progress';
	}

	return 'Not Started';
}

// Fun√ß√£o para identificar se uma issue √© um √©pico
function isEpic(issue) {
	const issueTitle = issue.title || '';

	// Debug: exibir os campos dispon√≠veis no issue
	console.log(`\nVerificando issue #${issue.number}: ${issueTitle}`);

	// Vamos criar um conjunto de crit√©rios para identificar √©picos
	let isEpicByType = false;
	let isEpicByLabel = false;
	let extractedIssueType = null;

	// Verificar se a issue tem um tipo expl√≠cito no campo issueType
	if (issue.issueType && issue.issueType.name) {
		console.log(`  Issue tem tipo expl√≠cito: ${issue.issueType.name}`);
		extractedIssueType = issue.issueType.name;

		// Verificar se o tipo √© Epic/√âpico
		if (issue.issueType.name.toLowerCase() === 'epic' || 
		    issue.issueType.name.toLowerCase() === '√©pico') {
			isEpicByType = true;
			console.log(`  ‚úì Issue confirmada como √âpico pelo tipo: ${issue.issueType.name}`);
		} else {
			console.log(`  ‚úó Issue n√£o √© √âpico (tipo: ${issue.issueType.name})`);
			// Se o tipo est√° explicitamente definido como algo diferente de Epic, n√£o √© um √©pico
			return { isEpic: false, issueType: extractedIssueType };
		}
	}

	// Verifica√ß√£o pelo tipo (se dispon√≠vel nos campos)
	if (!isEpicByType && issue.fieldValues && issue.fieldValues.nodes) {
		const epicTypeField = issue.fieldValues.nodes.find(
			(fieldValue) =>
				fieldValue.field &&
				(fieldValue.field.name === 'Type' ||
					fieldValue.field.name === 'Issue Type' ||
					fieldValue.field.name === 'Kind') &&
				fieldValue.name &&
				(fieldValue.name.toLowerCase() === 'epic' ||
					fieldValue.name.toLowerCase() === '√©pico'),
		);

		if (epicTypeField) {
			isEpicByType = true;
			extractedIssueType = epicTypeField.name;
			console.log(`  ‚úì Issue tem tipo √âpico via campo: ${epicTypeField.name}`);
		}
	}

	// Verifica se a issue tem labels que indicam explicitamente que √© um √©pico
	if (issue.labels && issue.labels.nodes) {
		const epicLabels = issue.labels.nodes.filter(
			(label) =>
				label.name &&
				(label.name.toLowerCase() === 'epic' ||
				 label.name.toLowerCase() === '√©pico' ||
				 label.name.toLowerCase() === 'epic-issue')
		);

		if (epicLabels.length > 0) {
			isEpicByLabel = true;
			console.log(`  ‚úì Issue tem label de √©pico: ${epicLabels.map(l => l.name).join(', ')}`);
			if (!extractedIssueType) {
				extractedIssueType = '√âpico'; // Se n√£o temos tipo expl√≠cito, mas temos uma label de √©pico
			}
		}
	}

	// Crit√©rios para decis√£o final:
	// 1. Se tem tipo expl√≠cito de √©pico, √© √©pico
	// 2. Se tem label expl√≠cita de √©pico, √© √©pico

	const isAnEpic = isEpicByType || isEpicByLabel;

	if (isAnEpic) {
		console.log(`  ‚úì Confirmado como √©pico: ${issue.title}`);
	} else {
		console.log(`  ‚úó N√£o √© √©pico: ${issue.title}`);
	}

	return { isEpic: isAnEpic, issueType: extractedIssueType };
}

// Fun√ß√£o principal de processamento
function processEpicsData(items) {
	console.log(`Processando ${items.length} itens para an√°lise de √©picos...`);

	if (items.length > 0) {
		console.log(
			'Estrutura do primeiro item:',
			JSON.stringify(items[0], null, 2),
		);
	}

	const epicsByStatus = {};
	const epicsDetails = [];
	let totalEpics = 0;
	let itemsProcessed = 0;
	let itemsSkipped = 0;

	// Processar cada item
	items.forEach((item, index) => {
		itemsProcessed++;

		// Pula itens sem conte√∫do
		if (!exists(item)) {
			console.log(`Item ${index} √© null/undefined`);
			itemsSkipped++;
			return;
		}

		if (!exists(item.content)) {
			console.log(`Item ${index} n√£o tem content:`, Object.keys(item));
			itemsSkipped++;
			return;
		}

		const issue = item.content;

		// Verifica se √© um √©pico (agora retorna um objeto com isEpic e issueType)
		const epicResult = isEpic(issue);
		console.log(
			`Item ${index} (${issue.title || 'sem t√≠tulo'}) √© √©pico: ${epicResult.isEpic}`,
		);

		if (!epicResult.isEpic) {
			itemsSkipped++;
			return;
		}

		totalEpics++;
		console.log(`√âpico encontrado: ${issue.title}`);

		// Obter informa√ß√µes do status do projeto
		let projectStatus = 'No Status';
		let priority = 'No Priority';
		let issueType = epicResult.issueType || 'No Type';  // Usar o tipo extra√≠do da fun√ß√£o isEpic

		// Debug log para verificar os campos dispon√≠veis
		if (index === 0 && exists(item.fieldValues?.nodes)) {
			console.log('Campos dispon√≠veis no primeiro item:');
			item.fieldValues.nodes.forEach((fieldValue) => {
				if (exists(fieldValue) && exists(fieldValue.field)) {
					console.log(
						`  Campo: ${fieldValue.field.name}, Valor: ${
							fieldValue.name ||
							fieldValue.title ||
							fieldValue.date ||
							'sem valor'
						}`,
					);
				}
			});
		}

		// Ainda verificar campos fieldValues para outros atributos como status e prioridade
		if (exists(item.fieldValues?.nodes)) {
			item.fieldValues.nodes.forEach((fieldValue) => {
				if (!exists(fieldValue) || !exists(fieldValue.field)) return;

				if (fieldValue.field.name === 'Status' && exists(fieldValue.name)) {
					projectStatus = fieldValue.name;
				}
				if (fieldValue.field.name === 'Priority' && exists(fieldValue.name)) {
					priority = fieldValue.name;
				}
				// Verificar campo de tipo apenas se ainda n√£o temos um tipo da fun√ß√£o isEpic
				if (
					issueType === 'No Type' &&
					(fieldValue.field.name === 'Type' ||
						fieldValue.field.name === 'Issue Type' ||
						fieldValue.field.name === 'Kind') &&
					exists(fieldValue.name)
				) {
					issueType = fieldValue.name;
					console.log(
						`Encontrado tipo "${issueType}" para issue: ${issue.title}`,
					);
				}
			});
		}

		// Verificar se a issue tem um tipo diretamente no conte√∫do como √∫ltima tentativa
		if (issue.type && issueType === 'No Type') {
			issueType = issue.type;
			console.log(`Tipo encontrado diretamente na issue: ${issueType}`);
		}

		// Atribu√≠mos o tipo "√âpico" apenas se a fun√ß√£o isEpic determinou que √© um √©pico
		// e n√£o temos outro tipo
		if (epicResult.isEpic && issueType === 'No Type') {
			issueType = '√âpico';
			console.log(
				`Definindo tipo como √âpico com base nos crit√©rios rigorosos: ${issue.title}`,
			);
		}

		// Determinar status do √©pico baseado no progresso das sub-issues
		const epicStatus = determineEpicStatus(issue.subIssuesSummary);

		// Contabilizar por status
		epicsByStatus[epicStatus] = (epicsByStatus[epicStatus] || 0) + 1;

		// Criar detalhes do √©pico
		const epicDetail = {
			epicId: issue.number ? issue.number.toString() : item.id,
			epicTitle: issue.title || 'Sem t√≠tulo',
			epicUrl: issue.url || '#',
			status: epicStatus,
			projectStatus: projectStatus,
			priority: priority,
			issueType: issueType, // Agora issueType est√° sendo extra√≠do corretamente do objeto issue.issueType
			completionRate: issue.subIssuesSummary
				? issue.subIssuesSummary.percentCompleted
				: 0,
			subIssuesTotal: issue.subIssuesSummary ? issue.subIssuesSummary.total : 0,
			subIssuesCompleted: issue.subIssuesSummary
				? issue.subIssuesSummary.completed
				: 0,
			subIssuesRemaining: issue.subIssuesSummary
				? issue.subIssuesSummary.total - issue.subIssuesSummary.completed
				: 0,
			assignees: issue.assignees?.nodes?.map((a) => a.login) || [],
			labels: issue.labels?.nodes?.map((l) => l.name) || [],
			updatedAt: issue.updatedAt || '',
			createdAt: issue.createdAt || '',
			// Descri√ß√£o removida para reduzir tamanho dos dados no n8n
		};

		epicsDetails.push(epicDetail);
	});

	console.log(`\nResumo do processamento:`);
	console.log(`- Items processados: ${itemsProcessed}`);
	console.log(`- Items ignorados: ${itemsSkipped}`);
	console.log(`- √âpicos encontrados: ${totalEpics}`);

	// Ordenar √©picos por taxa de conclus√£o (decrescente) e depois por data de atualiza√ß√£o
	epicsDetails.sort((a, b) => {
		if (b.completionRate !== a.completionRate) {
			return b.completionRate - a.completionRate;
		}
		return new Date(b.updatedAt) - new Date(a.updatedAt);
	});

	// Calcular estat√≠sticas adicionais
	const averageCompletion =
		totalEpics > 0
			? epicsDetails.reduce((sum, epic) => sum + epic.completionRate, 0) /
			  totalEpics
			: 0;

	const totalSubIssues = epicsDetails.reduce(
		(sum, epic) => sum + epic.subIssuesTotal,
		0,
	);
	const completedSubIssues = epicsDetails.reduce(
		(sum, epic) => sum + epic.subIssuesCompleted,
		0,
	);

	// Criar resumo final
	const summary = {
		projectTitle: 'Epic Status Report',
		totalEpics: totalEpics,
		epicsProgress: epicsByStatus,
		epicsDetails: epicsDetails,
		statistics: {
			averageCompletionRate: Math.round(averageCompletion * 100) / 100,
			totalSubIssues: totalSubIssues,
			completedSubIssues: completedSubIssues,
			overallProgress:
				totalSubIssues > 0
					? Math.round((completedSubIssues / totalSubIssues) * 100)
					: 0,
		},
		date: new Date().toISOString(),
		generatedAt: new Date().toLocaleString('pt-BR', {
			timeZone: 'America/Sao_Paulo',
			year: 'numeric',
			month: '2-digit',
			day: '2-digit',
			hour: '2-digit',
			minute: '2-digit',
		}),
	};

	return summary;
}

// Execu√ß√£o principal
try {
	let data;

	if (isLocalRun) {
		const fs = require('fs');
		// Ambiente local - ler do arquivo data.json
		console.log('Executando em ambiente local...');

		if (!fs.existsSync('data.json')) {
			console.error('Erro: Arquivo data.json n√£o encontrado!');
			console.log(
				'Execute o script github-query.sh primeiro para obter os dados.',
			);
			process.exit(1);
		}

		const rawData = fs.readFileSync('data.json', 'utf8');
		const jsonData = JSON.parse(rawData);

		// Verificar estrutura dos dados
		if (jsonData.data?.organization?.projectV2?.items?.nodes) {
			data = jsonData.data.organization.projectV2.items.nodes;
		} else if (Array.isArray(jsonData)) {
			data = jsonData;
		} else {
			throw new Error('Estrutura de dados inv√°lida no arquivo data.json');
		}
	} else {
		// Ambiente n8n - usar dados de entrada
		const inputData = $input.all();

		console.log(`Recebidos ${inputData.length} itens de entrada no n8n`);
		console.log(
			'Primeiro item recebido:',
			JSON.stringify(inputData[0]?.json, null, 2),
		);

		if (!inputData || inputData.length === 0) {
			throw new Error('Nenhum dado de entrada fornecido');
		}

		// Combinar dados de m√∫ltiplas p√°ginas se necess√°rio
		data = [];
		inputData.forEach((item, index) => {
			console.log(
				`Processando item ${index + 1}:`,
				Object.keys(item.json || {}),
			);

			if (item.json) {
				if (Array.isArray(item.json)) {
					console.log(
						`Item ${index + 1} √© array com ${item.json.length} elementos`,
					);
					data = data.concat(item.json);
				} else if (item.json.data?.organization?.projectV2?.items?.nodes) {
					const nodes = item.json.data.organization.projectV2.items.nodes;
					console.log(
						`Item ${index + 1} tem ${nodes.length} nodes na estrutura padr√£o`,
					);
					data = data.concat(nodes);
				} else if (item.json.projectTitle) {
					// Se j√° est√° processado (vindo de outro node), usar diretamente
					console.log(`Item ${index + 1} parece j√° processado`);
					data.push(item.json);
				} else {
					console.log(
						`Item ${
							index + 1
						} estrutura n√£o reconhecida, adicionando como est√°`,
					);
					data.push(item.json);
				}
			}
		});

		console.log(`Total de dados para processamento: ${data.length}`);
		if (data.length > 0) {
			console.log('Primeiro item de dados:', JSON.stringify(data[0], null, 2));
		}
	}

	// Processar os dados
	const result = processEpicsData(data);

	if (isLocalRun) {
		// Salvar resultado em arquivo
		const fs = require('fs');
		fs.writeFileSync(
			'epics_analysis_result.json',
			JSON.stringify(result, null, 2),
		);
		console.log('\n=== RESUMO DOS √âPICOS ===');
		console.log(`Total de √âpicos: ${result.totalEpics}`);
		console.log(
			`Taxa M√©dia de Conclus√£o: ${result.statistics.averageCompletionRate}%`,
		);
		console.log(`Progresso Geral: ${result.statistics.overallProgress}%`);
		console.log('\nProgresso por Status:');
		Object.entries(result.epicsProgress).forEach(([status, count]) => {
			console.log(`  ${status}: ${count} √©picos`);
		});
		console.log('\nResultado salvo em epics_analysis_result.json');

		// Exportar para n8n
		module.exports = { json: result };
	} else {
		// Retornar para n8n
		return { json: result };
	}
} catch (error) {
	console.error('Erro no processamento:', error.message);

	const errorResult = {
		error: true,
		message: `Erro ao processar dados dos √©picos: ${error.message}`,
		timestamp: new Date().toISOString(),
	};

	if (isLocalRun) {
		console.log(JSON.stringify(errorResult, null, 2));
		process.exit(1);
	} else {
		return { json: errorResult };
	}
}
